jQuery.hashslice=function(hash,elements,update){var list=jQuery.extend(true,[],update||hash),elms=typeof elements==='string'?elements.replace(/^\W+|\W+$/g,'').split(/\W+/):elements;jQuery.each(elms,function(elm){if(update)hash[elms[elm]]=list[elms[elm]];else list.push(list[elms[elm]]);});return update?hash:list;};